import { useState, useCallback } from 'react';
import Link from 'next/link';
import getConfig from 'next/config';
import { Avatar, Input, Layout } from 'antd';
import { GithubOutlined, UserOutlined } from '@ant-design/icons';
import { HeaderInner, HeaderLeft, githubIconStyle, footer } from './style';
import MyContainer from '../MyContainer';

const { publicRuntimeConfig } = getConfig();

const AppLayout = ({ children }) => {
  const [search, setSearch] = useState('');

  const handleSearchChange = useCallback(e => {
    setSearch(e.target.value);
  }, []); // 因为依赖于setSearch，而这个方法是不会改变的，所以可以直接传[]

  const handleOnSearch = useCallback(() => {}, []);

  return (
    <Layout>
      <Layout.Header>
        <MyContainer renderer={<HeaderInner />}>
          {/* left-logo/search */}
          <HeaderLeft>
            <Link href="/">
              <a>
                <GithubOutlined style={githubIconStyle} />
              </a>
            </Link>
            <div>
              <Input.Search
                onChange={handleSearchChange}
                onSearch={handleOnSearch}
                value={search}
                placeholder="搜索仓库"
              />
            </div>
          </HeaderLeft>

          {/* right-user */}
          <div className="header-right">
            <div className="user">
              <Avatar size={40} icon={<UserOutlined />} />
            </div>
          </div>
        </MyContainer>
      </Layout.Header>
      <Layout.Content>
        <MyContainer>{children}</MyContainer>
      </Layout.Content>
      <Layout.Footer style={footer}>
        Develop by ZJJ @
        <a href="https://github.com/zhangjunjie0730" target="__blank">
          github
        </a>
      </Layout.Footer>
      <style jsx global>
        {`
          #__next {
            height: 100%;
          }
          .ant-layout {
            min-height: 100%;
          }
          .ant-layout-header {
            padding-left: 0;
            padding-right: 0;
          }
          .ant-layout-content {
            background: #fff;
          }
        `}
      </style>
    </Layout>
  );
};

export default AppLayout;
