const UserInfo = () => {
  return <>userInfo</>;
};

UserInfo.getInitialProps = async ({ ctx }) => {
  const { userInfo } = ctx.session;
  if (userInfo) {
    ctx.body = userInfo;
    ctx.set('Content-Type', 'application/json');
  } else {
    ctx.status = 401;
    ctx.body = 'Need Login';
  }
  console.log('ctx data', ctx.data);
  return ctx.data;
};

export default UserInfo;
