import { request } from '../../utils/api';

const IssueDetail = ({ issues }) => {
  console.log(issues);
  return <span>issues</span>;
};

IssueDetail.getInitialProps = async ({ ctx }) => {
  const { owner, name } = ctx.query;
  const result = await request({ url: `/repos/${owner}/${name}/issues` }, ctx.req, ctx.res);

  return {
    issues: result.data,
  };
};

export default IssueDetail;
