import { useRouter } from 'next/router';
import DetailLayout from '../../components/DetailLayout';
import { request } from '../../utils/api';
import initCache from '../../utils/client-cache';

const { cache, useCache } = initCache({ genCacheKeyStrate: context => genCacheKeyStrate(context) });

function Detail({ readme }) {
  useCache(genCacheKeyStrate(router), { readme });

  return <div>detail index page</div>;
}

Detail.getInitialProps = async ({ ctx }) => {
  const {
    query: { owner, name },
    req,
    res,
  } = ctx;

  const { data } = await request({ url: `/repos/${owner}/${name}/readme` }, req, res);

  return {
    readme: data,
  };
};

export default DetailLayout(Detail);
