import { useState, useCallback } from 'react';
import Link from 'next/link';
import { Avatar, Input, Layout } from 'antd';
import { GithubOutlined, UserOutlined } from '@ant-design/icons';
import { HeaderInner, HeaderLeft, GithubIcon } from './style';

// 把icon样式固定在外部对象中的好处：每次重新渲染样式都拿到的是同一个对象，这样就不会重新渲染。
const githubIconStyle = {
  color: 'white',
  fontSize: 40,
  display: 'block',
  paddingTop: 10,
  marginRight: 20,
};

const AppLayout = ({ children }) => {
  const [search, setSearch] = useState('');

  const handleSearchChange = useCallback(e => {
    setSearch(e.target.value);
  }, []); // 因为依赖于setSearch，而这个方法是不会改变的，所以可以直接传[]

  const handleOnSearch = useCallback(() => {}, []);

  return (
    <Layout>
      <Layout.Header>
        <HeaderInner>
          {/* left-logo/search */}
          <HeaderLeft>
            <GithubIcon>
              <Link href="/">
                <a>
                  <GithubOutlined style={githubIconStyle} />
                </a>
              </Link>
            </GithubIcon>
            <div>
              <Input.Search
                onChange={handleSearchChange}
                onSearch={handleOnSearch}
                value={search}
                placeholder="搜索仓库"
              />
            </div>
          </HeaderLeft>

          {/* right-user */}
          <div className="header-right">
            <div className="user">
              <Avatar size={40} icon={<UserOutlined />} />
            </div>
          </div>
        </HeaderInner>
      </Layout.Header>
      <Layout.Content>{children}</Layout.Content>
      <Layout.Footer>
        Develop by ZJJ @{' '}
        <a href="https://github.com/zhangjunjie0730" target="__blank">
          github
        </a>
      </Layout.Footer>
    </Layout>
  );
};

export default AppLayout;
